export class ExamplePage {
  readonly page: Page;

  constructor(page: Page) {
    this.page = page;
  }

  public async closePage(): Promise<void> {
    this.page.close();
  }

  public async goToUrl(appUrl: string): Promise<void> {
    await navigateToUrl (this.page, appUrl, true);
